settings.css:
header .site-nav__item a,.mobile-nav__link,.currency-input{
text-transform:uppercase;
letter-spacing: 2px !important;
font-size: 14px !important;
}
.site-nav__link--underline:after{
border-bottom: 1px solid #ababab;
}
.header-item .currency-input{
background-image: url(https://cdn.shopify.com/s/files/1/0604/1370/6468/files/ico-select-menu.png?v=1634622081);
}


@media only screen and (min-width: 769px) {
.site-header:not(.site-header--stuck) {
    padding: 0px 0px 0px;
}
}

.footer__logo img {
max-width:100%;
width:auto;
height:auto;
}
.site-footer__linklist a{
font-family: 'Roboto', sans-serif;
font-weight: 500;
text-transform: uppercase;
letter-spacing: 3px;
color: #848484;
}
.footer__collapsible{
